import React from 'react';
import styled from 'styled-components';
import Lero from '../Frase';
import Botao from '../Botao';


const Click = async () => {
    const API = "https://testapi.io/api/matheusrod92/lero-lero"
    const res = await fetch(API)
    const data = await res.json();
    const randon = Math.random() * data.lenght;
    console.log(data[Math.floor(random)]);
}
const Texto = styled.p`
    display: flex;
    flex-wrap: wrap;
    width: 70%
    heigth: 60px;
    background: grey;

`;




function Tela (props){
    return (
        <div> 
            <Lero></Lero> 

            <Botao onClick={Click}></Botao>

        </div>
    );

}
export default Tela;
